function binarySearch(arr, x) {
  let low = 0, high = arr.length - 1;
  while (low <= high) {
    let mid = Math.floor((low + high) / 2);
    if (arr[mid] === x)
      return mid;
    if (arr[mid] < x)
      low = mid + 1;
    else
      high = mid - 1;
  }
  return -1;
}
